import { redirect } from "react-router-dom";

export function getAuthToken() {
  const token = localStorage.getItem('token');

  return token;
}

export function loader() {
  return getAuthToken();
}

export function checkToken(){
  const token = getAuthToken();

  if (!token) {
    return redirect('/auth')
  }
}
